// ここにコード書いていく
import { useState } from "react";

const App = () => {
  const [name, setName] = useState("No name");
  const nameChangeHandler = (e) => {
    setName((prev) => e.target.value);
  };
  return (
    <>
      <h1>Hello! {name}!</h1>
      <input type="text" value={name} onChange={nameChangeHandler}></input>
    </>
  );
};

export default App;
